
var base64icon1 = 'data:image/png;base64,'
var base64content = 'data:image/png;base64,'

var base64icon2 = 'data:image/png;base64,'


// 知道了按钮
$('body').on('click','.jianmei-close',function(e){
    $('.jianmei-show').fadeOut(function(){
      $('.jianmei-show').remove();
    });
})
// 获取version
chrome.runtime.sendMessage(
  {greeting: 'getVersion'},
  function(response) {
      if(response){
        var dom = `<div class='jianmei-show'style='
        width:450px;height:222px;
        background-color:#fff;
        padding:0 20px;
        position:fixed;top:10px;right:20px;
        z-index:9999;
        border-radius:3px;
        border:1px solid #aaa;
        box-shadow:2px 2px 2px #ccc
        display:none;
        '> 
      <div> 
        <p style='font-size:18px;
        font-family:PingFangSC-Medium;
        font-weight:500;
        margin-top:40px;
        margin-bottom:20px;
        color:rgba(23,31,36,1);
        line-height:20px;'><img style='width:26px;height:26px;margin-right:5px;vertical-align: middle;display:inline-block;' src="${base64icon1}" alt="">阅读助手 V<span id='version'>${chrome.runtime.getManifest().version}</span> 已安装成功</p>
      </div>
      <div style='font-size:14px;
      font-family:PingFangSC-Regular;
      font-weight:400;
      color:rgba(34,34,34,1);
      line-height:20px;'>点击简媒注册插件（下图所示）可以查看数据、添加账号哦。</div>
      <img src="${base64content}" style='height:80px;display:inline-block;'>
      <div style='display:flex;
           justify-content:space-between;'>
        <p>
            <img src="${base64icon2}" style='width:64px;height:20pxdisplay:inline-block;'>
        </p>
        <p  class='jianmei-close'
            style='font-size:14px;
            font-family:PingFangSC-Regular;
            font-weight:400;
            color:rgba(93,100,121,1);
            line-height:20px;
            cursor: pointer;'>知道了</p>
      </div>
    </div>`
        document.getElementsByTagName('body')[0].innerHTML += dom;
        $('.jianmei-show').fadeIn();
      }
  }
);



